import React, {useState} from 'react';
import { Card, TextField, Button, Grid } from '@material-ui/core';
import { useFormik } from "formik";
import * as Yup from "yup";
import './style.css';
import axios from 'axios';

const validationSchema = Yup.object().shape({
    search: Yup.string()
})

function Searchbar () {

    const [result, setResult] = useState([]);
    const [apiKey, setApiKey] = useState("AIzaSyBnAsEf6nQxgoZchu_-fhVa3qleLyxjhuo")
    
    const formik = useFormik({
            initialValues: {
                search: 'Search'
            },
        validationSchema: validationSchema,
        onSubmit: (values) => {
            const newSearch ={
                book: formik.values.search
            }
            console.log(newSearch);
            
            axios.get('https://www.googleapis.com/books/v1/volumes?q='+newSearch.book+'&key='+apiKey+'&maxResults=40')
            .then(data => {
                console.log(data)
            })
        }
    })

    function handleChange(event) {
        const book = event.target.value;
        setBook(book);
    }

    return(
        <Grid
            container
            justify='center'
        >
        <Card>
            <form onSubmit={formik.handleSubmit}>
            <TextField
                variant="outlined"
                id="search"
                placeholder="Search Book Here"
                fullWidth
                onChange={formik.handleChange}
                value={formik.values.search}
            />
            <Button color='primary' id='bttn' variant='contained' type='submit' fullWidth>Search</Button>
        </form>
        </Card>
        </Grid>
    )
}

export default Searchbar
